CREATE TABLE restaurant (id INT AUTO_INCREMENT NOT NULL, gg_place_id VARCHAR(255) NOT NULL, name VARCHAR(255) NOT NULL, address VARCHAR(255) NOT NULL, locality VARCHAR(255) NOT NULL, locality_slug VARCHAR(255) NOT NULL, country VARCHAR(255) NOT NULL, slug_slug VARCHAR(255) NOT NULL, location_lat DOUBLE PRECISION NOT NULL, location_lng DOUBLE PRECISION NOT NULL, international_phone_number VARCHAR(255) NOT NULL, slug VARCHAR(255) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX gg_place_id_idx (gg_place_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;